(42, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Bảo vệ Của hàng WooCommerce của bạn bằng Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2023-04-04 06:42:36', NULL, 0, 'thumbnail', 'http://localhost/UDNM/wordpress/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(43, 'tap_to_pay_iphone_q2_2023_no_wcpay', 'marketing', 'en_US', 'Accept in-person contactless payments on your iPhone', 'Tap to Pay on iPhone and WooCommerce Payments is quick, secure, and simple to set up — no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.', '{}', 'unactioned', 'woocommerce.com', '2023-04-04 06:42:37', NULL, 0, 'plain', '', 0, 0, 'info'),
